import { observer } from "mobx-react-lite";
import React, { useEffect, useState } from "react";
import { Accordion, Button, Form } from "react-bootstrap";


const Task= observer((props)=>{
    const [checked, setChecked] =useState(false)

    useEffect(()=>{
        setChecked(props.done)
    }, [])

    return(
        <div className="d-flex justify-content-between align-items-center">
            <Form className="me-3">
                <Form.Check checked={checked} onChange={(e)=>{
                    fetch("http://localhost:3006/tasks/change?id="+localStorage.getItem('id') + "&cat=" + props.category+"&task="+props.task)
                    .then(
                        setChecked(!checked)
                    )


                }}/>
            </Form>
            {checked ? <div style={{textDecoration: "line-through", fontSize:18}}>{props.task}</div>: <div style={{fontSize:20}}>{props.task}</div>}
            <Button variant="danger" style={{width: 38, fontSize: 20}}>x</Button>
        </div>
    )
})

export default Task;
