import { observer } from "mobx-react-lite";
import React, { useState } from "react";
import {Navbar, Button} from 'react-bootstrap';
import CategoryCreation from "./CategoryCreation";
import TaskCreation from "./TaskCreation";

const Topbar = observer(() =>{
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function Modals(){
        if(show === 1) return <CategoryCreation handleClose={handleClose}/>
        if(show === 2) return <TaskCreation />
        return <div></div>
    }

    return(
        <div>
        <Modals />
        <Navbar bg="dark" variant="dark" className="p-2 d-flex justify-content-center">
            <Button className="me-2" onClick={() => setShow(1)}>Новая категория</Button>
            <Button>Новая задача</Button>
        </Navbar>
        </div>
    )
})

export default Topbar;
